sp_widget.client_script:
function($rootScope,$scope) {
	/* widget controller */
	var c = this;
	c.selectItem = function(idx) {
		var id = c.data.notes[idx].sys_id;
		console.log('Note ID: ' + id);
		$rootScope.noteID = id;
		$rootScope.$emit('selectNote', id);
	}
	$rootScope.$on('refreshNote', function(event,data) {
		console.log('Asking server to update note');
		c.server.get({
			action: 'updateNote',
			noteID: data
		})
	})
}

sp_widget.script:
(function() {
	/* populate the 'data' object */
	/* e.g., data.table = $sp.getValue('table'); */
	data.notes = [];
	if(input && input.noteID){
		if(input.action === "updateNote"){
			console.log('Server updating note');
			var gr = new GlideRecord('x_snc_createnotes_note');
			if(gr.get(input.noteID)){
				console.log(data.notes);
				for(var note in data.notes){
					console.log(note.sys_id, input.noteID);
					if(note.sys_id === input.noteID){
						console.log('note found updating...');
						note = {};
						$sp.getRecordDisplayValues(note, gr, 'number,title,sys_id');
						break;
					}
				}
			}
		}
	} else {
		//Get the note records for the current user
		var curUserNotes = new GlideRecord('x_snc_createnotes_note');
		curUserNotes.addQuery('user', gs.getUser().getID());
		curUserNotes.orderByDesc('sys_created_on');
		curUserNotes.query();
		//Add notes to data payload
		while(curUserNotes.next()){
			noteObj = {};
			$sp.getRecordDisplayValues(noteObj, curUserNotes, 'number,title,sys_id');
			noteObj.note = curUserNotes.note.slice(0,20);
			data.notes.push(noteObj);
		}
	}
})();
